import React, { Component } from 'react';
import Success from './success'

class Popup extends Component {
    constructor(props) {
        super(props);
        this.state = {
            openPopup: false
        }
        this.handlePopup = this.handlePopup.bind(this);
    }

    handlePopup() {
        this.setState({
            openPopup: !this.state.openPopup
        })
    }

    render() {
        const { openPopup } = this.state;
        if(openPopup) {
            return (
                <div className="modal modal-mask" style="display: block">
                    <div className="modal-dialog" role="document">
                        <div className="modal-content">
                            <div className="modal-header div-responsive">
                                <div className="modal-title"><h4>Message Box</h4></div>
                                <button type="button" className="close" click="hide" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">&times;</span>
                                </button>
                            </div>
                            <div className="modal-body div-responsive">
                                <Success message="message" />
                            </div>
                            <div className="modal-footer div-responsive">
                                <button type="button" className="btn btn-secondary" click="hide">Close</button>
                            </div>
                        </div>
                    </div>
                </div>
            );
        }
    }
}

export default Popup;
