import React, { Component } from 'react';
import Error from './error';

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            submitted: null,
            email: null,
            password: null,
            error: null
        }
        this.login = this.login.bind(this);
    }

    login() {

    }

    render() {
        const { submitted, email, password, error } = this.state;
        return (
            <div className="container">
                <h1>Sign In</h1>
                <br/>
                <form onSubmit="handleLogin">
                    <div className="div-responsive">
                        <label for="email">Email:</label>
                        <input type="text" onChange="handleChange" placeholder="Email" className={"form-control " + (submitted && !email) ? 'is-invalid': ''} />
                        <div className="alert-field">
                            {
                                (submitted && !email) && <div className="invalid-feedback">Email is required</div>
                            }
                        </div>
                    </div>
                    <div className="div-responsive">
                        <label for="password">Password:</label>
                        <input type="password" onChange="handleChange" placeholder="Password" className={"form-control " + (submitted && !password) ? 'is-invalid': ''} />
                        <div className="alert-field">
                            {
                                (submitted && !password) && <div className="invalid-feedback">Password is required</div>
                            }
                        </div>
                    </div>
                    <div className="div-responsive">
                        <a href="reset_password" className="customLink">Want to reset password ?</a>
                    </div>
                    <div className="alert-box div-responsive">
                        {
                            error.message != null ? <Error message="error.message" /> : null
                        }
                    </div>
                    <div className="div-responsive">
                        <button type="submit" className="btn btn-secondary btn-big">Login</button>
                    </div>
                </form>
            </div>
        )
    }
}

export default Login;
