import React, { Component } from 'react';
import Error from './error';

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            submitted: false,
            email: "",
            password: "",
            error: null
        }
        this.handleLogin = this.handleLogin.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleLogin(e) {
        e.preventDefault();
        this.setState({
            submitted: true
        })

        if(this.email && this.password) {

        }
    }

    handleChange(e) {
        switch(e.target.id) {
            case 'email': this.setState({
                email: e.target.value
            });
            break;
            case 'password': this.setState({
                password: e.target.value
            });
            default: break;
        }
    }

    render() {
        const { submitted, email, password, error } = this.state;
        return (
            <div className="container">
                <div className="login">
                    <h1>Sign In</h1>
                    <br/>
                    <form onSubmit={this.handleLogin}>
                        <div className="div-responsive">
                            <label htmlFor="email">Email:</label>
                            <input id="email" value={email} type="text" onChange={this.handleChange} placeholder="Email" className={(submitted && !email) ? "form-control is-invalid" : "form-control"} />
                            <div className="alert-field">
                                {
                                    (submitted && !email) && <div className="invalid-feedback">Email is required</div>
                                }
                            </div>
                        </div>
                        <div className="div-responsive">
                            <label htmlFor="password">Password:</label>
                            <input id="password" value={password} type="password" onChange={this.handleChange} placeholder="Password" className={(submitted && !email) ? "form-control is-invalid" : "form-control"} />
                            <div className="alert-field">
                                {
                                    (submitted && !password) && <div className="invalid-feedback">Password is required</div>
                                }
                            </div>
                        </div>
                        <div className="div-responsive">
                            <a href="reset_password" className="customLink">Want to reset password ?</a>
                        </div>
                        <div className="alert-box div-responsive">
                            {
                                (error != null && error.message != null) && <Error message="error.message" />
                            }
                        </div>
                        <div className="div-responsive">
                            <button type="submit" className="btn btn-secondary btn-big">Login</button>
                        </div>
                    </form>
                </div>
            </div>
        )
    }
}

export default Login;
